fun fibonacci(n: Int): Long {
	return if (n <= 1) {
		n.toLong()
	} else {
		var a = 0L
		var b = 1L
		var result = 0L

		for (i in 2..n) {
			result = a + b
			a = b
			b = result
		}

		result
		}
	}
